const sections=document.querySelectorAll(".section");function getCenteredSection(){let e=window.innerHeight/2,t=null,s=1/0;return sections.forEach(c=>{let r=c.getBoundingClientRect(),n=r.top+r.height/2,o=Math.abs(n-e);o<s&&(s=o,t=c)}),t}window.addEventListener("scroll",()=>{let e=getCenteredSection();document.querySelectorAll(".hero-menu, .projects-menu, .about-me-menu, .contacts-menu, .burger_content .hero-menu, .burger_content .projects-menu, .burger_content .about-me-menu, .burger_content .contacts-menu").forEach(e=>{e.classList.remove("selected")}),"hero"==e.classList[0]?(document.querySelector(".hero-menu").classList.add("selected"),document.querySelector(".burger_content .hero-menu").classList.add("selected")):"projects"==e.classList[0]?(document.querySelector(".projects-menu").classList.add("selected"),document.querySelector(".burger_content .projects-menu").classList.add("selected")):"skills"==e.classList[0]||"about-me"==e.classList[0]?(document.querySelector(".about-me-menu").classList.add("selected"),document.querySelector(".burger_content .about-me-menu").classList.add("selected")):(document.querySelector(".contacts-menu").classList.add("selected"),document.querySelector(".burger_content .contacts-menu").classList.add("selected"))});